'''Created on 30 05 2021@author: Enzo Cocca'''from builtins import objectclass ANCHOR_POINT(object):    # def __init__"    def __init__(self,                 gid,                 the_geom,                 site,                 code,                                  years,                 link,                 type,                 obj                 ):        self.gid=gid        self.the_geom=the_geom        self.site=site        self.code=code                         self.years=years        self.link=link        self.type=type        self.obj=obj            # def __repr__"    def __repr__(self):        return "<ANCHOR_POINT('%d','%s', '%s',%s,'%s','%s', '%s', '%s')>" % (            self.gid,            self.the_geom,            self.site,            self.code,                             self.years,            self.link,            self.type,            self.obj        )