'''Created on 19 feb 2018@author: Enzo Cocca'''from builtins import objectclass POTTERY(object):    #def __init__"    def __init__(self,                id_rep,                 divelog_id,                site,                date_,                artefact_id,                photographed,                drawing,                retrieved,                inclusions,                percent_inclusion,                specific_part,                form,                typology,                provenance,                munsell_clay,                surf_treatment,                conservation,                depth,                storage_,                period,                state,                samples,                washed,                dm,                dr,                db,                 th,                ph,                bh,                thickmin,                thickmax,                years,                box,                biblio,                description,                area,                munsell_surf,                category,                wheel_made,                qty                ):        self.id_rep= id_rep        self.divelog_id=divelog_id        self.site=site        self.date_=date_        self.artefact_id=artefact_id        self.photographed=photographed        self.drawing=drawing        self.retrieved=retrieved        self.inclusions=inclusions        self.percent_inclusion=percent_inclusion        self.specific_part=specific_part        self.form=form        self.typology=typology        self.provenance=provenance        self.munsell_clay=munsell_clay        self.surf_treatment=surf_treatment        self.conservation=conservation        self.depth=depth        self.storage_=storage_        self.period=period        self.state=state        self.samples=samples        self.washed=washed        self.dm =dm        self.dr=dr        self.db=db              self.th=th        self.ph=ph        self.bh=bh        self.thickmin=thickmin        self.thickmax=thickmax        self.years=years        self.box=box        self.biblio=biblio        self.description=description        self.area=area        self.munsell_surf=munsell_surf        self.category=category        self.wheel_made=wheel_made        self.qty=qty    def __repr__(self):        return "<POTTERY('%d','%d','%s','%s','%s',%s','%s','%s','%s', '%s','%s','%s','%s','%s','%s','%s','%s','%s','%s','%s','%s','%s','%s','%s','%s','%s','%s','%s','%s','%s','%s','%d','%d','%s','%s','%s','%s','%s','%s','%d')>" % (        self.id_rep,        self.divelog_id,        self.site,        self.date_,        self.artefact_id,        self.photographed,        self.drawing,        self.retrieved,        self.inclusions,        self.percent_inclusion,        self.specific_part,        self.form,        self.typology,        self.provenance,        self.munsell_clay,        self.surf_treatment,        self.conservation,        self.depth,        self.storage_,        self.period,        self.state,        self.samples,        self.washed,        self.dm,        self.dr,        self.db,        self.th,        self.ph,        self.bh,        self.thickmin,        self.thickmax,        self.years,        self.box,        self.biblio,        self.description,        self.area,        self.munsell_surf,        self.category,        self.wheel_made,        self.qty        )