'''Created on 30 05 2021@author: Enzo Cocca'''from builtins import objectclass POTTERY_POINT(object):    # def __init__"    def __init__(self,                 gid,                 the_geom,                 site,                 code,                                  years,                 link,                 type,                 x,                 y,                 rotation,                                  layer                 ):        self.gid=gid        self.the_geom=the_geom        self.site=site        self.code=code                         self.years=years        self.link=link        self.type=type        self.x=x        self.y=y        self.rotation=rotation                         self.layer=layer    # def __repr__"    def __repr__(self):        return "<POTTERY_POINT('%d','%s', '%s',%s,'%s','%s', '%f', '%f', '%f','%s',%d)>" % (            self.gid,            self.the_geom,            self.site,            self.code,                             self.years,            self.link,            self.type,            self.x,            self.y,            self.rotation,                             self.layer        )