'''Created on 30 05 2021@author: Enzo Cocca'''from builtins import objectclass SITE_POLYGON(object):    # def __init__"    def __init__(self,                 gid,                 name_feat,                                  photo,                 photo2,                 photo3,                 photo4,                 photo5,                 photo6,                 location,                 the_geom,                 coord                 ):        self.gid = gid  # 0        self.name_feat=name_feat        self.photo=photo        self.photo2=photo2        self.photo3=photo3        self.photo4=photo4        self.photo5=photo5        self.photo6=photo6        self.location = location # 1        self.the_geom = the_geom  # 2        self.coord=coord    # def __repr__"    def __repr__(self):        return "<SITE_POLYGON('%d','%s', '%s',%s,'%s','%s', '%s', '%s', '%s','%s','%s')>" % (            self.gid,            self.name_feat,            self.photo,            self.photo2,            self.photo3,            self.photo4,            self.photo5,            self.photo6,            self.location,            self.the_geom,            self.coord        )