'''Created on 30 05 2021@author: Enzo Cocca'''from builtins import objectclass TRANSECT_POLYGON(object):    # def __init__"    def __init__(self,                 gid,                 id,                 name_tr,                 location,                 area,                                  photo,                 photo2,                 photo3,                 photo4,                 photo5,                 photo6,                                  the_geom                 ):        self.gid = gid  # 0        self.id = id  # 0                self.name_tr=name_tr        self.location = location # 1        self.area = area # 1        self.photo=photo        self.photo2=photo2        self.photo3=photo3        self.photo4=photo4        self.photo5=photo5        self.photo6=photo6                self.the_geom = the_geom  # 2            # def __repr__"    def __repr__(self):        return "<TRANSECT_POLYGON('%d','%d', '%s',%s,'%s','%s', '%s', '%s', '%s','%s','%s','%s')>" % (            self.gid ,            self.id ,            self.name_tr,            self.location ,            self.area,            self.photo,            self.photo2,            self.photo3,            self.photo4,            self.photo5,            self.photo6,                  self.the_geom        )